import React from 'react'
import { forwardRef } from 'react'
import './AboutMe.css'
import { Link } from 'react-router-dom';
import axios from 'axios'
import fileDownload from 'js-file-download'

const AboutMe=forwardRef(({Contact},AboutMe) =>{
  handleDownload = (url, filename) => {
    axios.get(url, {
      responseType: 'blob',
    })
    .then((res) => {
      fileDownload(res.data, filename)
    })
  }
  return (
    <div ref={AboutMe} className='about'>  
        <div className='text'>
            <h1 className={'name'}> 
                Konstantinos Varnalidis 
            </h1>
            <h2 className="stack">
              Full-Stack Enginner
            </h2>
            <p>
              I am an Full-Stack developer with a passion to learn.My biggest advantage is my ability to teach my self new skills.
              I love coding and basketball and right now i am try to develop a stat keeping app for Euroleague and NBA.
            </p>
              <Link to="/files/Resume.pdf" target="_blank" download>Download</Link>
            <button class="button" onClick={()=>Contact.current.scrollIntoView({behavior:"smooth"})} ><span>Contact Me </span></button>
        </div>
    </div>
  )
});

export default AboutMe
