import React, { forwardRef,useRef } from 'react';
import './Footer.css';
import emailjs from '@emailjs/browser';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEnvelope,faPhone} from '@fortawesome/free-solid-svg-icons';
import { faLinkedin } from '@fortawesome/free-brands-svg-icons';
import Mailto from 'react-mailto-link';
const  Footer=forwardRef((props,ref)=> {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_ugsx12n', 'template_8r84nd9', form.current, '4ih39sMyMnOpB2CeL')
      .then((result) => {
          console.log(result.text);
          e.target.reset();
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <div className='contact-container'>
      <div className='contact-info'>
        <h4>Contact Information</h4>
          <div className='icon-text'>
          <FontAwesomeIcon className='fa' icon={faPhone}/>
            <span className='infotext'>+30 6946007022</span>
          </div>
          <div className='icon-text'>
          <FontAwesomeIcon className='fa' icon={faEnvelope}/>
          <Mailto className='infotext' email='kostasvarnalidis@gmail.com' obfuscated={false}>kostasvarnalidis</Mailto>
          </div>
          <div className='icon-text'>
          <FontAwesomeIcon className='fa' icon={faLinkedin} />
          <a className='infotext' href="https://www.linkedin.com/in/kostas-varnalidis-779838256">KVarnalidis</a>
          </div>
      </div>
      <div ref={ref}>
        <form  ref={form} onSubmit={sendEmail}>
          <div className='cform'>
            <label>Name</label>
            <input type="text" name="user_name" />
            <label>Email</label>
            <input type="email" name="user_email" />
            <label>Message</label>
            <textarea name="message" />
            <input type="submit" value="Send" />
            </div>
        </form>
      </div>
    </div>
  );
})
