import React, { forwardRef,useState,useRef } from 'react';
import './Footer.css';
import emailjs from '@emailjs/browser';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEnvelope, faMailBulk, faPhone,faLinkedInIn  } from '@fortawesome/free-solid-svg-icons';
import { faLinkedin } from '@fortawesome/free-brands-svg-icons';
const  Footer=forwardRef((props,ref)=> {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_ugsx12n', 'template_8r84nd9', form.current, '4ih39sMyMnOpB2CeL')
      .then((result) => {
          console.log(result.text);
          e.target.reset();
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <div className='contact-container'>
      <div className='contact-info'>
        <h4>Contact Information</h4>
          <p>text</p>
          <div className='icon-text'>
          <FontAwesomeIcon icon={faPhone}/>
            <span>text</span>
          </div>
          <div className='icon-text'>
          <FontAwesomeIcon icon={faEnvelope}/>
            <span>text</span>
          </div>
          <div className='icon-text'>
          <FontAwesomeIcon icon={faLinkedin} />
          <a href="https://www.linkedin.com/in/kostas-varnalidis-779838256">KVarnalidis</a>
          </div>
          <div className='social-media'>
            <a href='#' className='icon-circle'>
              <i className='icon'></i>
            </a>
          </div>
      </div>
      <div ref={ref}>
        <form className='cform' ref={form} onSubmit={sendEmail}>
        <label>Name</label>
          <input type="text" name="user_name" />
          <label>Email</label>
          <input type="email" name="user_email" />
          <label>Message</label>
          <textarea name="message" />
          <input className='Sendbutton'type="submit" value="Send" />
        </form>
      </div>
    </div>
  );
})
